/**
 * The type of the Grid pager.
 *
 * The available values are:
 * * `numeric`&mdash;Buttons with numbers.
 * * `input`&mdash;Input for typing the page number.
 *
 * @example
 * ```html-no-run
 *
 * <kendo-grid
 *    [pageable]="{
 *        type: 'numeric'
 *    }">
 * </kendo-grid>
 * ```
 */
export declare type PagerType = 'numeric' | 'input';
/**
 * The pager settings of the Grid.
 *
 * @example
 * ```html-no-run
 *
 * <kendo-grid
 *    [pageable]="{
 *        buttonCount: 2,
 *        info: false
 *    }">
 * </kendo-grid>
 * ```
 */
export interface PagerSettings {
    /**
     * Sets the maximum numeric buttons count before the buttons are collapsed.
     *
     * @default 10
     */
    buttonCount?: number;
    /**
     * Toggles the information about the current page and the total number of records.
     *
     * @default true
     */
    info?: boolean;
    /**
     * Defines the type of the Grid pager.
     *
     * @default 'numeric'
     */
    type?: PagerType;
    /**
     * Shows a menu for selecting the page size.
     *
     * @default false
     */
    pageSizes?: boolean | Array<number>;
    /**
     * Toggles the **Previous** and **Next** buttons.
     *
     * @default true
     */
    previousNext?: boolean;
    /**
     * Toggles the built-in responsive behavior of the Pager.
     * Available in version `5.0.0` and above ([see example]({% slug paging_grid %})).
     *
     * @default true
     */
    responsive?: boolean;
}
